USE [BancoTPC]GOSET ANSI_NULLS ONGOSET QUOTED_IDENTIFIER ONGO-- Criar tabela de usu√°riosCREATE TABLE [dbo].[tb_usuario] (    [id_usuario] INT IDENTITY(1,1) NOT NULL,    [us_nome] VARCHAR(100) NOT NULL,    [us_email] VARCHAR(100) NOT NULL,    [us_senha] VARCHAR(50) NOT NULL,    CONSTRAINT [PK_tb_usuario] PRIMARY KEY CLUSTERED ([id_usuario] ASC)) ON [PRIMARY]GO-- Criar tabela de tarefasCREATE TABLE [dbo].[tb_tarefa] (    [id_tarefa] INT IDENTITY(1,1) NOT NULL,    [ta_titulo] VARCHAR(50) NOT NULL,    [ta_descricao] VARCHAR(200) NOT NULL,    [ta_status] VARCHAR(50) NOT NULL,    [fk_usuario] INT NOT NULL,    CONSTRAINT [PK_tb_tarefa] PRIMARY KEY CLUSTERED ([id_tarefa] ASC),    CONSTRAINT [FK_tb_tarefa_tb_usuario] FOREIGN KEY ([fk_usuario])        REFERENCES [dbo].[tb_usuario] ([id_usuario])) ON [PRIMARY]GO